package datatypes;

public class arithmatic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//+,-,/,%,*
		
			float x=4.5f;
			float y=12.4f;
			System.out.println(x+y);
			
			System.out.println(x-y);
			
			System.out.println(x/y);
			
			System.out.println(x%y);
			
			System.out.println(x*y);
			
		}

	
	}


